import { USER_INFO_UPDATE } from "../Constant/Reducer/Reducer";

export default function reducer(
    state={
        userDetails:{
            email:"",
            id:"",
            role:"",
            schoolID:"",
            status:"",
            userName:""
        } 
    }
    ,action
){
    switch(action.type){
            case USER_INFO_UPDATE:
                    return {
                        ...state,
                        userDetails:action.data
                    }
            default:
                break;
    }
}
