import { FETCHING ,STOP_LOADER,SHOW_ALERT,HIDE_ALERT,SHOW_DIALOG,HIDE_DIALOG,SHOW_SNACK_BAR,HIDE_SNACK_BAR} from "../Constant/Reducer/Reducer.js"


export default function reducer(

    state={

        loader:{
            isLoading:false,
            loaderMessage:""
        },
        dialog:{
            isDialog:false,
            dialogTitle:"",
            dialogMessage:"",
        },

        snackBar:{
            isSnackBar:false,
            snackBarMessage: "",

        },
        alert:{
            isAlert:false,
            alertMessage:"",
            alertTitle:"",
        }



    }
    ,action
){
    switch (action.type) {
        case SHOW_SNACK_BAR:
          return {
            ...state,
            snackBar:{
                snackBarMessage: action.message,
                isSnackBar:true 
            }
          };

        case HIDE_SNACK_BAR:
          return {
            ...state,
            snackBar:{
                snackBarMessage: "",
                isSnackBar:false 
            }
          };

        case FETCHING:
          return {
            ...state,
            fetching: true,
            loaderMessage: action.data.loaderMessage,
          };
        case STOP_LOADER:
          return {
            ...state,
            fetching: false,
            loaderMessage: "",
          };
        case SHOW_ALERT:
          return {
            ...state,
            showAlert: true,
            alertTitle: action.data.title,
            alertMessage: action.data.message,
          };
        case HIDE_ALERT:
          return {
            ...state,
            alertTitle: "",
            alertMessage: "",
            showAlert: false,
          };
        case SHOW_DIALOG:
          return {
            ...state,
            showDialog: true,
            dialogTitle: action.data.title,
            dialogMessage: action.data.message,
            callbackDialog: action.data.callback,
          };
        case HIDE_DIALOG:
          return {
            ...state,
            showDialog: false,
            dialogTitle: "",
            dialogMessage: "",
            callbackDialog: {},
          };
        default:
          break;
      }
    return state
}
