import { SUPER_ADMIN } from "../Constant/Constant";
import { superAdminMenuList } from "../Constant/MenuList";
import { LOGIN_ACTION, USER_INFO_UPDATE, USER_SIDEMENU } from "../Constant/Reducer/Reducer";

export function updateUserInfo(userDetail){
    return {
        type:USER_INFO_UPDATE,
        data:userDetail
    }
}

export function loginAction(userDetail){

    let sideMenu=[]
    const {role}=userDetail
    if(role===SUPER_ADMIN){
        sideMenu=superAdminMenuList
    }
    return {
        type:LOGIN_ACTION,
        userDetail:userDetail,
        sideMenu:sideMenu
    }
}

export function assignSideMenu({role}){

    if(role===SUPER_ADMIN){
        return {
            type:USER_SIDEMENU,
            data:superAdminMenuList
        }
    }

}
